test_that("read_data works with CSV", {
  # Use system.file to locate the file in the installed package
  csv_file <- system.file("CWP_dataset.csv", package = "CWP.dataset")
  
  # Check if the file path is valid
  expect_true(nzchar(csv_file), info = "CSV file not found")
  
  # Check if the file exists
  expect_true(file.exists(csv_file), info = "CSV file does not exist")
  
  # Read the file and test if it's correctly read as a data frame
  data <- read_data(csv_file)
  expect_true(is.data.frame(data), info = "Data should be a data.frame or data.table")
})
